import{_ as e,o as t,c as a,ae as o}from"./chunks/framework.xMPsnH9w.js";const h=JSON.parse('{"title":"Item Management Guide","description":"","frontmatter":{},"headers":[],"relativePath":"guide/item-management.md","filePath":"guide/item-management.md"}'),l={name:"guide/item-management.md"};function r(s,i,n,c,u,d){return t(),a("div",null,[...i[0]||(i[0]=[o(`<h1 id="item-management-guide" tabindex="-1">Item Management Guide <a class="header-anchor" href="#item-management-guide" aria-label="Permalink to &quot;Item Management Guide&quot;">​</a></h1><p>Learn how to effectively manage lost and found items on the platform.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The item management system allows users to report lost items, report found items, and manage their submissions throughout the lifecycle of each item.</p><h2 id="reporting-lost-items" tabindex="-1">Reporting Lost Items <a class="header-anchor" href="#reporting-lost-items" aria-label="Permalink to &quot;Reporting Lost Items&quot;">​</a></h2><h3 id="step-by-step-process" tabindex="-1">Step-by-Step Process <a class="header-anchor" href="#step-by-step-process" aria-label="Permalink to &quot;Step-by-Step Process&quot;">​</a></h3><ol><li><p><strong>Navigate to Report Lost Item</strong></p><ul><li>Click &quot;Report Lost Item&quot; from the main menu</li><li>Or use the quick action button on the dashboard</li></ul></li><li><p><strong>Fill in Item Details</strong></p><ul><li><strong>Item Name</strong>: Clear, descriptive name</li><li><strong>Category</strong>: Select appropriate category</li><li><strong>Description</strong>: Detailed description including: <ul><li>Color, size, brand</li><li>Distinguishing features</li><li>Serial numbers (if applicable)</li></ul></li><li><strong>Location</strong>: Where the item was lost</li><li><strong>Date Lost</strong>: When you last had the item</li><li><strong>Contact Preference</strong>: How you want to be contacted</li></ul></li><li><p><strong>Upload Photos</strong> (Optional but recommended)</p><ul><li>Clear photos help with identification</li><li>Multiple angles if possible</li><li>Close-ups of unique features</li></ul></li><li><p><strong>Submit Report</strong></p><ul><li>Review all information</li><li>Click &quot;Submit Lost Item Report&quot;</li><li>Receive confirmation</li></ul></li></ol><h3 id="best-practices-for-lost-items" tabindex="-1">Best Practices for Lost Items <a class="header-anchor" href="#best-practices-for-lost-items" aria-label="Permalink to &quot;Best Practices for Lost Items&quot;">​</a></h3><ul><li><strong>Be specific</strong>: More details increase chances of recovery</li><li><strong>Act quickly</strong>: Report as soon as possible</li><li><strong>Check regularly</strong>: Monitor for matching found items</li><li><strong>Update status</strong>: Mark as found when recovered</li></ul><h2 id="reporting-found-items" tabindex="-1">Reporting Found Items <a class="header-anchor" href="#reporting-found-items" aria-label="Permalink to &quot;Reporting Found Items&quot;">​</a></h2><h3 id="step-by-step-process-1" tabindex="-1">Step-by-Step Process <a class="header-anchor" href="#step-by-step-process-1" aria-label="Permalink to &quot;Step-by-Step Process&quot;">​</a></h3><ol><li><p><strong>Navigate to Report Found Item</strong></p><ul><li>Click &quot;Report Found Item&quot; from the main menu</li><li>Or use the quick action button</li></ul></li><li><p><strong>Fill in Item Details</strong></p><ul><li><strong>Item Name</strong>: What you found</li><li><strong>Category</strong>: Select category</li><li><strong>Description</strong>: Detailed description</li><li><strong>Location Found</strong>: Exact location</li><li><strong>Date Found</strong>: When you found it</li><li><strong>Current Location</strong>: Where item is stored</li></ul></li><li><p><strong>Upload Photos</strong> (Required)</p><ul><li>Clear photos are essential</li><li>Multiple angles</li><li>Any identifying marks or features</li></ul></li><li><p><strong>Submit Report</strong></p><ul><li>Review information</li><li>Click &quot;Submit Found Item Report&quot;</li><li>Item goes to admin for approval</li></ul></li></ol><h3 id="best-practices-for-found-items" tabindex="-1">Best Practices for Found Items <a class="header-anchor" href="#best-practices-for-found-items" aria-label="Permalink to &quot;Best Practices for Found Items&quot;">​</a></h3><ul><li><strong>Secure the item</strong>: Keep it safe</li><li><strong>Report immediately</strong>: Don&#39;t delay</li><li><strong>Provide accurate location</strong>: Help owner retrieve it</li><li><strong>Respond to claims</strong>: Check notifications regularly</li></ul><h2 id="searching-for-items" tabindex="-1">Searching for Items <a class="header-anchor" href="#searching-for-items" aria-label="Permalink to &quot;Searching for Items&quot;">​</a></h2><h3 id="basic-search" tabindex="-1">Basic Search <a class="header-anchor" href="#basic-search" aria-label="Permalink to &quot;Basic Search&quot;">​</a></h3><ol><li><strong>Use the search bar</strong> at the top of the page</li><li><strong>Enter keywords</strong> related to your item</li><li><strong>View results</strong> and click for details</li></ol><h3 id="advanced-filtering" tabindex="-1">Advanced Filtering <a class="header-anchor" href="#advanced-filtering" aria-label="Permalink to &quot;Advanced Filtering&quot;">​</a></h3><p>Filter by:</p><ul><li><strong>Category</strong>: Electronics, Documents, Personal Items, etc.</li><li><strong>Date Range</strong>: When item was lost/found</li><li><strong>Location</strong>: Specific buildings or areas</li><li><strong>Organization</strong>: Filter by organization</li><li><strong>Status</strong>: Active, Claimed, Returned</li></ul><h3 id="search-tips" tabindex="-1">Search Tips <a class="header-anchor" href="#search-tips" aria-label="Permalink to &quot;Search Tips&quot;">​</a></h3><ul><li>Use specific keywords</li><li>Try different search terms</li><li>Check multiple categories</li><li>Expand date range if needed</li><li>Contact admin if you think an item matches</li></ul><h2 id="managing-your-items" tabindex="-1">Managing Your Items <a class="header-anchor" href="#managing-your-items" aria-label="Permalink to &quot;Managing Your Items&quot;">​</a></h2><h3 id="view-your-items" tabindex="-1">View Your Items <a class="header-anchor" href="#view-your-items" aria-label="Permalink to &quot;View Your Items&quot;">​</a></h3><ol><li>Navigate to &quot;My Items&quot; in your profile</li><li>See all your reported items: <ul><li>Lost items you&#39;ve reported</li><li>Found items you&#39;ve reported</li><li>Claims you&#39;ve submitted</li></ul></li></ol><h3 id="update-item-status" tabindex="-1">Update Item Status <a class="header-anchor" href="#update-item-status" aria-label="Permalink to &quot;Update Item Status&quot;">​</a></h3><p><strong>For Lost Items:</strong></p><ul><li>Mark as &quot;Found&quot; when recovered</li><li>Update description if needed</li><li>Add additional photos</li><li>Delete if no longer relevant</li></ul><p><strong>For Found Items:</strong></p><ul><li>Update location if moved</li><li>Add more details if needed</li><li>Mark as &quot;Claimed&quot; when returned</li></ul><h3 id="item-lifecycle" tabindex="-1">Item Lifecycle <a class="header-anchor" href="#item-lifecycle" aria-label="Permalink to &quot;Item Lifecycle&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Lost Item Flow:</span></span>
<span class="line"><span>Report → Active → [Matched] → Claimed → Returned → Closed</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Found Item Flow:</span></span>
<span class="line"><span>Report → Pending Approval → Active → Claimed → Returned → Closed</span></span></code></pre></div><h2 id="claiming-items" tabindex="-1">Claiming Items <a class="header-anchor" href="#claiming-items" aria-label="Permalink to &quot;Claiming Items&quot;">​</a></h2><h3 id="how-to-submit-a-claim" tabindex="-1">How to Submit a Claim <a class="header-anchor" href="#how-to-submit-a-claim" aria-label="Permalink to &quot;How to Submit a Claim&quot;">​</a></h3><ol><li><strong>Find the item</strong> in search results</li><li><strong>Click &quot;Claim This Item&quot;</strong></li><li><strong>Provide verification details</strong>: <ul><li>Proof of ownership</li><li>Additional description</li><li>Serial numbers or unique identifiers</li><li>Purchase receipts (if available)</li></ul></li><li><strong>Submit claim</strong></li><li><strong>Wait for admin review</strong></li></ol><h3 id="verification-process" tabindex="-1">Verification Process <a class="header-anchor" href="#verification-process" aria-label="Permalink to &quot;Verification Process&quot;">​</a></h3><p>Admins will:</p><ul><li>Review your claim details</li><li>Compare with item description</li><li>May request additional information</li><li>Contact you for verification</li><li>Approve or deny claim</li></ul><h3 id="after-claim-approval" tabindex="-1">After Claim Approval <a class="header-anchor" href="#after-claim-approval" aria-label="Permalink to &quot;After Claim Approval&quot;">​</a></h3><ol><li><strong>Receive notification</strong> of approval</li><li><strong>Coordinate pickup</strong> with finder or admin</li><li><strong>Verify item</strong> when collecting</li><li><strong>Confirm receipt</strong> in the system</li><li><strong>Provide feedback</strong> (optional)</li></ol><h2 id="item-categories" tabindex="-1">Item Categories <a class="header-anchor" href="#item-categories" aria-label="Permalink to &quot;Item Categories&quot;">​</a></h2><h3 id="electronics" tabindex="-1">Electronics <a class="header-anchor" href="#electronics" aria-label="Permalink to &quot;Electronics&quot;">​</a></h3><ul><li>Phones, tablets, laptops</li><li>Chargers and accessories</li><li>Headphones, earbuds</li><li>Cameras, USB drives</li></ul><h3 id="personal-items" tabindex="-1">Personal Items <a class="header-anchor" href="#personal-items" aria-label="Permalink to &quot;Personal Items&quot;">​</a></h3><ul><li>Wallets, purses</li><li>Keys, keychains</li><li>Jewelry, watches</li><li>Glasses, sunglasses</li></ul><h3 id="documents" tabindex="-1">Documents <a class="header-anchor" href="#documents" aria-label="Permalink to &quot;Documents&quot;">​</a></h3><ul><li>IDs, passports</li><li>Student cards</li><li>Certificates, diplomas</li><li>Important papers</li></ul><h3 id="clothing-accessories" tabindex="-1">Clothing &amp; Accessories <a class="header-anchor" href="#clothing-accessories" aria-label="Permalink to &quot;Clothing &amp; Accessories&quot;">​</a></h3><ul><li>Jackets, coats</li><li>Bags, backpacks</li><li>Shoes, boots</li><li>Hats, scarves</li></ul><h3 id="books-stationery" tabindex="-1">Books &amp; Stationery <a class="header-anchor" href="#books-stationery" aria-label="Permalink to &quot;Books &amp; Stationery&quot;">​</a></h3><ul><li>Textbooks, notebooks</li><li>Pens, pencils</li><li>Calculators</li><li>Art supplies</li></ul><h3 id="sports-equipment" tabindex="-1">Sports Equipment <a class="header-anchor" href="#sports-equipment" aria-label="Permalink to &quot;Sports Equipment&quot;">​</a></h3><ul><li>Balls, rackets</li><li>Gym bags</li><li>Uniforms</li><li>Protective gear</li></ul><h2 id="notifications" tabindex="-1">Notifications <a class="header-anchor" href="#notifications" aria-label="Permalink to &quot;Notifications&quot;">​</a></h2><h3 id="email-notifications" tabindex="-1">Email Notifications <a class="header-anchor" href="#email-notifications" aria-label="Permalink to &quot;Email Notifications&quot;">​</a></h3><p>You&#39;ll receive emails for:</p><ul><li>Potential matches for your lost items</li><li>Claims on your found items</li><li>Status updates on your claims</li><li>Messages from admins</li><li>Weekly summaries</li></ul><h3 id="managing-notifications" tabindex="-1">Managing Notifications <a class="header-anchor" href="#managing-notifications" aria-label="Permalink to &quot;Managing Notifications&quot;">​</a></h3><ol><li>Go to <strong>Settings → Notifications</strong></li><li>Choose notification preferences: <ul><li>Email frequency</li><li>SMS alerts (if available)</li><li>Push notifications</li><li>Weekly summaries</li></ul></li></ol><h2 id="privacy-and-security" tabindex="-1">Privacy and Security <a class="header-anchor" href="#privacy-and-security" aria-label="Permalink to &quot;Privacy and Security&quot;">​</a></h2><h3 id="what-information-is-visible" tabindex="-1">What Information is Visible <a class="header-anchor" href="#what-information-is-visible" aria-label="Permalink to &quot;What Information is Visible&quot;">​</a></h3><p><strong>Public Information:</strong></p><ul><li>Item description (limited)</li><li>Category and location</li><li>Date lost/found</li><li>Photos (may be blurred)</li></ul><p><strong>Private Information:</strong></p><ul><li>Your full contact details</li><li>Serial numbers</li><li>Detailed descriptions</li><li>Claim verification details</li></ul><h3 id="protecting-your-privacy" tabindex="-1">Protecting Your Privacy <a class="header-anchor" href="#protecting-your-privacy" aria-label="Permalink to &quot;Protecting Your Privacy&quot;">​</a></h3><ul><li>Don&#39;t include sensitive information in public descriptions</li><li>Use the messaging system for private details</li><li>Report suspicious claims to admins</li><li>Verify identity before meeting to exchange items</li></ul><h2 id="tips-for-success" tabindex="-1">Tips for Success <a class="header-anchor" href="#tips-for-success" aria-label="Permalink to &quot;Tips for Success&quot;">​</a></h2><h3 id="for-lost-items" tabindex="-1">For Lost Items <a class="header-anchor" href="#for-lost-items" aria-label="Permalink to &quot;For Lost Items&quot;">​</a></h3><ol><li><strong>Report immediately</strong>: Don&#39;t wait</li><li><strong>Be detailed</strong>: More information helps</li><li><strong>Check regularly</strong>: New items are added daily</li><li><strong>Expand search</strong>: Try different keywords</li><li><strong>Contact admin</strong>: If you think you found a match</li></ol><h3 id="for-found-items" tabindex="-1">For Found Items <a class="header-anchor" href="#for-found-items" aria-label="Permalink to &quot;For Found Items&quot;">​</a></h3><ol><li><strong>Secure the item</strong>: Keep it safe</li><li><strong>Report quickly</strong>: Owner may be searching</li><li><strong>Provide details</strong>: Help owner identify it</li><li><strong>Respond promptly</strong>: Check claims regularly</li><li><strong>Coordinate safely</strong>: Meet in public places</li></ol><h3 id="for-claims" tabindex="-1">For Claims <a class="header-anchor" href="#for-claims" aria-label="Permalink to &quot;For Claims&quot;">​</a></h3><ol><li><strong>Provide proof</strong>: Documentation helps</li><li><strong>Be specific</strong>: Describe unique features</li><li><strong>Be patient</strong>: Verification takes time</li><li><strong>Be honest</strong>: False claims are tracked</li><li><strong>Say thanks</strong>: Appreciate the finder</li></ol><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="item-not-appearing" tabindex="-1">Item Not Appearing <a class="header-anchor" href="#item-not-appearing" aria-label="Permalink to &quot;Item Not Appearing&quot;">​</a></h3><ul><li>Check if it&#39;s pending admin approval</li><li>Verify all required fields were filled</li><li>Contact admin if delayed</li></ul><h3 id="can-t-find-your-item" tabindex="-1">Can&#39;t Find Your Item <a class="header-anchor" href="#can-t-find-your-item" aria-label="Permalink to &quot;Can&#39;t Find Your Item&quot;">​</a></h3><ul><li>Try different search terms</li><li>Expand date range</li><li>Check multiple categories</li><li>Contact admin for assistance</li></ul><h3 id="claim-not-processed" tabindex="-1">Claim Not Processed <a class="header-anchor" href="#claim-not-processed" aria-label="Permalink to &quot;Claim Not Processed&quot;">​</a></h3><ul><li>Ensure all verification details provided</li><li>Check for admin messages</li><li>Allow time for manual review</li><li>Contact admin if urgent</li></ul><h3 id="photo-upload-issues" tabindex="-1">Photo Upload Issues <a class="header-anchor" href="#photo-upload-issues" aria-label="Permalink to &quot;Photo Upload Issues&quot;">​</a></h3><ul><li>Check file size (max 5MB)</li><li>Use supported formats (JPG, PNG)</li><li>Ensure stable internet connection</li><li>Try different browser if issues persist</li></ul><h2 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to &quot;Getting Help&quot;">​</a></h2><ul><li><strong>Help Center</strong>: Built-in documentation</li><li><strong>Contact Admin</strong>: Use messaging system</li><li><strong>FAQ</strong>: Common questions answered</li><li><strong>Support Email</strong>: For technical issues</li></ul><p>This guide covers the essential aspects of item management. For more specific scenarios or advanced features, consult the admin or refer to organization-specific guidelines.</p>`,86)])])}const p=e(l,[["render",r]]);export{h as __pageData,p as default};
