import{_ as e,o as t,c as a,ae as o}from"./chunks/framework.xMPsnH9w.js";const h=JSON.parse('{"title":"Quick Start Guide","description":"","frontmatter":{},"headers":[],"relativePath":"guide/quick-start.md","filePath":"guide/quick-start.md"}'),r={name:"guide/quick-start.md"};function l(n,i,s,c,u,d){return t(),a("div",null,[...i[0]||(i[0]=[o('<h1 id="quick-start-guide" tabindex="-1">Quick Start Guide <a class="header-anchor" href="#quick-start-guide" aria-label="Permalink to &quot;Quick Start Guide&quot;">​</a></h1><p>Get up and running with the Public Sector Lost &amp; Found Management Platform in minutes.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>This quick start guide will walk you through:</p><ol><li>Accessing the platform</li><li>Creating your first organization</li><li>Reporting a lost item</li><li>Reporting a found item</li><li>Processing a claim</li></ol><h2 id="step-1-access-the-platform" tabindex="-1">Step 1: Access the Platform <a class="header-anchor" href="#step-1-access-the-platform" aria-label="Permalink to &quot;Step 1: Access the Platform&quot;">​</a></h2><ol><li>Open your browser and navigate to the platform URL</li><li>You&#39;ll see the landing page with language options</li><li>Select your preferred language (English, Amharic, Oromo, or Tigrinya)</li></ol><h2 id="step-2-user-registration" tabindex="-1">Step 2: User Registration <a class="header-anchor" href="#step-2-user-registration" aria-label="Permalink to &quot;Step 2: User Registration&quot;">​</a></h2><h3 id="for-regular-users" tabindex="-1">For Regular Users <a class="header-anchor" href="#for-regular-users" aria-label="Permalink to &quot;For Regular Users&quot;">​</a></h3><ol><li>Click <strong>&quot;Sign Up&quot;</strong> on the homepage</li><li>Fill in your details: <ul><li>Full Name</li><li>Email Address</li><li>Phone Number</li><li>Password</li></ul></li><li>Click <strong>&quot;Create Account&quot;</strong></li><li>Check your email for verification link</li><li>Click the verification link to activate your account</li></ol><h3 id="for-organization-registration" tabindex="-1">For Organization Registration <a class="header-anchor" href="#for-organization-registration" aria-label="Permalink to &quot;For Organization Registration&quot;">​</a></h3><ol><li>Click <strong>&quot;Register Organization&quot;</strong></li><li>Fill in organization details: <ul><li>Organization Name</li><li>Type (University, Government, Hospital, etc.)</li><li>Contact Information</li><li>Address</li></ul></li><li>Submit for verification</li><li>Wait for admin approval</li></ol><h2 id="step-3-report-a-lost-item" tabindex="-1">Step 3: Report a Lost Item <a class="header-anchor" href="#step-3-report-a-lost-item" aria-label="Permalink to &quot;Step 3: Report a Lost Item&quot;">​</a></h2><ol><li><strong>Login</strong> to your account</li><li>Click <strong>&quot;Report Lost Item&quot;</strong></li><li>Fill in the item details: <ul><li>Item Name</li><li>Category (Electronics, Documents, Clothing, etc.)</li><li>Description</li><li>Location where lost</li><li>Date lost</li><li>Upload photo (optional)</li></ul></li><li>Click <strong>&quot;Submit Report&quot;</strong></li></ol><p>Your lost item will be added to the database and searchable by others.</p><h2 id="step-4-report-a-found-item" tabindex="-1">Step 4: Report a Found Item <a class="header-anchor" href="#step-4-report-a-found-item" aria-label="Permalink to &quot;Step 4: Report a Found Item&quot;">​</a></h2><ol><li>Click <strong>&quot;Report Found Item&quot;</strong></li><li>Fill in the details: <ul><li>Item Name</li><li>Category</li><li>Description</li><li>Location where found</li><li>Date found</li><li>Upload photo</li></ul></li><li>Click <strong>&quot;Submit Report&quot;</strong></li></ol><p>The found item will be visible to users searching for lost items.</p><h2 id="step-5-search-for-items" tabindex="-1">Step 5: Search for Items <a class="header-anchor" href="#step-5-search-for-items" aria-label="Permalink to &quot;Step 5: Search for Items&quot;">​</a></h2><h3 id="browse-all-items" tabindex="-1">Browse All Items <a class="header-anchor" href="#browse-all-items" aria-label="Permalink to &quot;Browse All Items&quot;">​</a></h3><ol><li>Click <strong>&quot;Browse Items&quot;</strong> on the homepage</li><li>Use filters to narrow down: <ul><li>Category</li><li>Date range</li><li>Location</li><li>Organization</li></ul></li></ol><h3 id="advanced-search" tabindex="-1">Advanced Search <a class="header-anchor" href="#advanced-search" aria-label="Permalink to &quot;Advanced Search&quot;">​</a></h3><ol><li>Use the search bar at the top</li><li>Enter keywords related to your item</li><li>Apply filters for better results</li></ol><h2 id="step-6-submit-a-claim" tabindex="-1">Step 6: Submit a Claim <a class="header-anchor" href="#step-6-submit-a-claim" aria-label="Permalink to &quot;Step 6: Submit a Claim&quot;">​</a></h2><p>When you find an item that might be yours:</p><ol><li>Click on the item card</li><li>Review the details carefully</li><li>Click <strong>&quot;Claim This Item&quot;</strong></li><li>Provide verification details: <ul><li>Proof of ownership</li><li>Additional description</li><li>Contact information</li></ul></li><li>Submit the claim</li></ol><h2 id="step-7-admin-functions" tabindex="-1">Step 7: Admin Functions <a class="header-anchor" href="#step-7-admin-functions" aria-label="Permalink to &quot;Step 7: Admin Functions&quot;">​</a></h2><h3 id="organization-admin-access" tabindex="-1">Organization Admin Access <a class="header-anchor" href="#organization-admin-access" aria-label="Permalink to &quot;Organization Admin Access&quot;">​</a></h3><p>If you&#39;re an organization admin:</p><ol><li>Navigate to <code>/admin/login</code></li><li>Login with admin credentials</li><li>Access admin dashboard with: <ul><li>User management</li><li>Item oversight</li><li>Organization settings</li><li>Reports and analytics</li></ul></li></ol><h3 id="super-admin-access" tabindex="-1">Super Admin Access <a class="header-anchor" href="#super-admin-access" aria-label="Permalink to &quot;Super Admin Access&quot;">​</a></h3><p>Super admins can:</p><ol><li>Manage all organizations</li><li>Approve organization registrations</li><li>System-wide configuration</li><li>Global reports</li></ol><h2 id="common-workflows" tabindex="-1">Common Workflows <a class="header-anchor" href="#common-workflows" aria-label="Permalink to &quot;Common Workflows&quot;">​</a></h2><h3 id="lost-item-recovery-process" tabindex="-1">Lost Item Recovery Process <a class="header-anchor" href="#lost-item-recovery-process" aria-label="Permalink to &quot;Lost Item Recovery Process&quot;">​</a></h3><ol><li><strong>User reports lost item</strong> → Item added to database</li><li><strong>Someone finds similar item</strong> → Reports found item</li><li><strong>System suggests potential matches</strong> → Notifications sent</li><li><strong>Owner submits claim</strong> → Verification process begins</li><li><strong>Admin verifies claim</strong> → Item marked as claimed</li><li><strong>Parties coordinate pickup</strong> → Case closed</li></ol><h3 id="organization-onboarding" tabindex="-1">Organization Onboarding <a class="header-anchor" href="#organization-onboarding" aria-label="Permalink to &quot;Organization Onboarding&quot;">​</a></h3><ol><li><strong>Organization applies</strong> → Registration form submitted</li><li><strong>Admin reviews application</strong> → Verification process</li><li><strong>Organization approved</strong> → Account activated</li><li><strong>Admin users created</strong> → Access credentials provided</li><li><strong>System configured</strong> → Branding and settings applied</li><li><strong>Training provided</strong> → Users onboarded</li></ol><h2 id="tips-for-success" tabindex="-1">Tips for Success <a class="header-anchor" href="#tips-for-success" aria-label="Permalink to &quot;Tips for Success&quot;">​</a></h2><h3 id="for-users" tabindex="-1">For Users <a class="header-anchor" href="#for-users" aria-label="Permalink to &quot;For Users&quot;">​</a></h3><ul><li><strong>Be detailed</strong>: More description helps with matching</li><li><strong>Upload photos</strong>: Visual identification is crucial</li><li><strong>Check regularly</strong>: New items are added daily</li><li><strong>Verify claims</strong>: Provide proof of ownership</li></ul><h3 id="for-admins" tabindex="-1">For Admins <a class="header-anchor" href="#for-admins" aria-label="Permalink to &quot;For Admins&quot;">​</a></h3><ul><li><strong>Monitor claims</strong>: Review and verify promptly</li><li><strong>Manage users</strong>: Keep user accounts updated</li><li><strong>Generate reports</strong>: Track performance metrics</li><li><strong>Maintain data</strong>: Regular cleanup of old items</li></ul><h2 id="language-switching" tabindex="-1">Language Switching <a class="header-anchor" href="#language-switching" aria-label="Permalink to &quot;Language Switching&quot;">​</a></h2><p>The platform supports multiple languages:</p><ol><li><strong>Header Language Selector</strong>: Switch anytime using the language buttons</li><li><strong>Persistent Selection</strong>: Your choice is remembered</li><li><strong>Complete Translation</strong>: All interface elements are translated</li><li><strong>RTL Support</strong>: Proper text direction for applicable languages</li></ol><h2 id="mobile-usage" tabindex="-1">Mobile Usage <a class="header-anchor" href="#mobile-usage" aria-label="Permalink to &quot;Mobile Usage&quot;">​</a></h2><p>The platform is fully responsive:</p><ul><li><strong>Touch-friendly</strong>: Optimized for mobile interaction</li><li><strong>Fast loading</strong>: Efficient on mobile networks</li><li><strong>Offline capability</strong>: Basic functionality without internet</li><li><strong>Push notifications</strong>: Stay updated on claims</li></ul><h2 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to &quot;Getting Help&quot;">​</a></h2><h3 id="user-support" tabindex="-1">User Support <a class="header-anchor" href="#user-support" aria-label="Permalink to &quot;User Support&quot;">​</a></h3><ul><li><strong>Help Center</strong>: Access built-in help documentation</li><li><strong>Contact Form</strong>: Reach out to organization admins</li><li><strong>FAQ Section</strong>: Common questions and answers</li><li><strong>Video Tutorials</strong>: Step-by-step guides</li></ul><h3 id="admin-support" tabindex="-1">Admin Support <a class="header-anchor" href="#admin-support" aria-label="Permalink to &quot;Admin Support&quot;">​</a></h3><ul><li><strong>Admin Documentation</strong>: Comprehensive admin guides</li><li><strong>System Logs</strong>: Monitor platform activity</li><li><strong>User Analytics</strong>: Track usage patterns</li><li><strong>Technical Support</strong>: Contact system administrators</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><p>Now that you&#39;re familiar with the basics:</p><ol><li><a href="./user-registration.html">User Registration Guide</a> - Detailed registration process</li><li><a href="./organization-setup.html">Organization Setup</a> - Configure your organization</li><li><a href="./item-management.html">Item Management</a> - Advanced item handling</li><li><a href="./claims-process.html">Claims Process</a> - Detailed claim workflow</li><li><a href="/lostAndFound-frontend/admin/">Admin Guide</a> - Administration features</li></ol><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="common-issues" tabindex="-1">Common Issues <a class="header-anchor" href="#common-issues" aria-label="Permalink to &quot;Common Issues&quot;">​</a></h3><p><strong>Can&#39;t login?</strong></p><ul><li>Check email verification</li><li>Reset password if needed</li><li>Contact admin for account issues</li></ul><p><strong>Item not appearing?</strong></p><ul><li>Check if it&#39;s pending approval</li><li>Verify all required fields were filled</li><li>Contact admin if issues persist</li></ul><p><strong>Claim not processed?</strong></p><ul><li>Ensure all verification details provided</li><li>Check with organization admin</li><li>Allow time for manual review</li></ul><p><strong>Email notifications not working?</strong></p><ul><li>Check spam folder</li><li>Verify email address in profile</li><li>Contact admin to check email settings</li></ul>',67)])])}const m=e(r,[["render",l]]);export{h as __pageData,m as default};
